INSERT INTO `db-tesis2`.`Question` VALUES (36,3,NULL,'La organizacion hace uso de On Line Transaction Processing OLTP (Son bases de datos orientadas al procesamiento de transacciones, puede involucrar operaciones de insercion, modificacion y borrado de datos. El proceso transaccional es tipico de las bases de datos operacionales).');
