INSERT INTO `db-tesis2`.`Question` VALUES (39,3,NULL,'La organizacion hace uso de Groupware (Se refiere a los programas informaticos que integran el trabajo en un solo proyecto con muchos usuarios concurrentes que se encuentran en diversas estaciones de trabajo, conectadas a traves de la red).');
